package com.brigelab_daytwo;

public class whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int num = 5, i = 1, sum = 0;

	        while(i <= num)
	        {
	            sum += i;
	            i++;
	        }

	        System.out.println("Sum = " + sum);
	    }
	}


